import { Entity, PrimaryGeneratedColumn, Column, CreateDateColumn } from "typeorm";

@Entity()
export class SearchResult {
  @PrimaryGeneratedColumn()
  id: number;

  @Column()
  searchQuery: string;

  @Column({ type: "json" })
  results: object; // Or a more specific type if your results have a consistent structure

  @CreateDateColumn()
  timestamp: Date;
}