import express, { Request, Response } from "express";
import { createConnection } from "typeorm";
import { SearchResult } from "./entities/SearchResult";

const app = express();
app.use(express.json());

createConnection().then(async (connection) => {
  const searchResultRepository = connection.getRepository(SearchResult);

  app.get("/search/:query", async (req: Request, res: Response) => {
    const results = await searchResultRepository.find({ where: { searchQuery: req.params.query } });
    res.json(results);
  });

  app.post("/search", async (req: Request, res: Response) => {
    const newResult = new SearchResult();
    newResult.searchQuery = req.body.searchQuery;
    newResult.results = req.body.results;
    await searchResultRepository.save(newResult);
    res.status(201).json(newResult);
  });

  // Add PUT and DELETE endpoints for updating and deleting search results

  app.listen(3000, () => console.log("Server started on port 3000"));
}).catch(error => console.error("TypeORM connection error:", error));
